import React from "react";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
import './App.css';
import Card from "./Card.js"
import data from "./data.js"

const renderIntro = () => {
  return (
    <div>
      <nav>
        <a href="https://leonmaksin.me/" class="h3">leonmaksin.me</a>
      </nav>
      <div class="introduction mt-3">
        <h1>Hello, I'm <a href="#aboutme">Leon</a></h1>
        <p class="mt-3">Welcome, I'm glad you found your way here! I'm a problem-solver that loves to create. 
        But instead of talking about myself, I'd rather just show you:</p>
      </div>
    </div>
  )
}

const renderClosing = () => {
  return (
    <div id="aboutme" class="closing mt-5">
      <h3>a little more about me</h3>
      <p class="mt-3">I am a student at the University of Michigan majoring in Computer Science and Business Administration, 
      and minoring in Mathematics. I've always had an intense passion for creating, and have built anything from rockets 
      to physics simulations to WebGL websites. I also have strong interests in startup, product, growth, software development, 
      and innovation in the fields of aerospace, edtech, community-building, and breaking down barriers to entry in general. 
      I strive to create as much as possible, and in my free time, I enjoy reading, exercising, playing card games and chess, 
      and spending time with my five younger siblings. Contact me at 
      <a class="email-link" href="mailto: lmaksin@umich.edu">lmaksin@umich.edu</a>, 
      I love making new friends!</p>
    </div>
  )
}

function App() {
  const cards = data.map(function(cardData) {
    return <Card
      key={cardData.id}
      input={cardData}
    />
  })
  return (
    <div>
      { renderIntro() }
      { cards }
      { renderClosing() }
    </div>
  );
}

export default App;
